o2_exp:
tf.tmp.imageRotateNum = 0;
tf.tmp.imageRotate = setInterval(function(){
	tf.tmp.imageRotateNum+=0.001;
	if (o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(2)].scaleX < 3){
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(2)].scaleX += tf.tmp.imageRotateNum/1000;
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(2)].scaleY += tf.tmp.imageRotateNum/1000;
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(2)].rect.y = tf.tmp.imageRotateNum*100;
		o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(2)].scaleX += tf.tmp.imageRotateNum/1000;
		o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(2)].scaleY += tf.tmp.imageRotateNum/1000;
		o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(2)].rect.y = tf.tmp.imageRotateNum*100;
	}
	o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(0)].rotation = tf.tmp.imageRotateNum;
},25);

tf.tmp.imageBureRandom = function(){
	return Math.floor(Math.random()*5)-2;
};
tf.tmp.getImageBureRect = function(x,y){
	tf.tmp.imageBureRectX = x;
	tf.tmp.imageBureRectY = y;
};
tf.tmp.getImageBureRect(0,-300);
tf.tmp.imageBure = setInterval(function(){
	o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(1)].rect.x = tf.tmp.imageBureRectX+tf.tmp.imageBureRandom();
	o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(1)].rect.y = tf.tmp.imageBureRectY+tf.tmp.imageBureRandom();
},25);

tf.tmp.imageMovePos = {
	X : 0.316,
	Y : 1
};
tf.tmp.imageMove = setInterval(function(){
	if (o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(0)].rect.y <= -40){
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(0)].rect.x += tf.tmp.imageMovePos.X;
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(0)].rect.y += tf.tmp.imageMovePos.Y;
	}else{
		clearInterval(tf.tmp.imageMove);
	}
},80);
