o2_exp:
tf.tmp.imageRotateNum = 0;
tf.tmp.imageZoom = setInterval(function(){
	tf.tmp.imageRotateNum+=0.0001;
	if (o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(0)].scaleX < 2){
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(0)].scaleX += tf.tmp.imageRotateNum/1000;
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(0)].scaleY += tf.tmp.imageRotateNum/1000;
		o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(0)].scaleX += tf.tmp.imageRotateNum/1000;
		o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(0)].scaleY += tf.tmp.imageRotateNum/1000;
	}
},25);

	tf.tmp.imageRotateNum = 0;
	tf.tmp.imageRotate = setInterval(function(){
		tf.tmp.imageRotateNum+=0.001;
		for (var i = 5; i < 8; i++) {
			if (o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(i)].scaleX < 3){
				o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(i)].scaleX += tf.tmp.imageRotateNum/1000;
				o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(i)].scaleY += tf.tmp.imageRotateNum/1000;
//				o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(i)].rect.y = tf.tmp.imageRotateNum*30;
				o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(i)].scaleX += tf.tmp.imageRotateNum/1000;
				o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(i)].scaleY += tf.tmp.imageRotateNum/1000;
//				o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(i)].rect.y = tf.tmp.imageRotateNum*30;
			}
		}

		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(2)].rotation = tf.tmp.imageRotateNum;
		if (o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(2)].opacity <= 1){
			o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(2)].opacity = tf.tmp.imageRotateNum*4;
		}
	},25);
