tf.tmp.imageRotateNum = 0;
tf.tmp.imageZoom = setInterval(function(){
	tf.tmp.imageRotateNum+=0.001;
	if (o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(3)].scaleX < 2){
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(3)].scaleX += tf.tmp.imageRotateNum/1000;
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(3)].scaleY += tf.tmp.imageRotateNum/1000;
		o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(3)].scaleX += tf.tmp.imageRotateNum/1000;
		o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(3)].scaleY += tf.tmp.imageRotateNum/1000;
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(3)].rotation += tf.tmp.imageRotateNum/1000;
	}
	if (o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(4)].scaleX < 2){
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(4)].scaleX += tf.tmp.imageRotateNum/1000;
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(4)].scaleY += tf.tmp.imageRotateNum/1000;
