		o2.backLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(4)].scaleY += tf.tmp.imageRotateNum/1000;
		o2.foreLayers.imageLayers[f.ghostpia.imageLayerManager.getLayerNumById(4)].rotation -= tf.tmp.imageRotateNum/1000;
	}
},25);

